    public int[] findBall(int[][] grid) {
        int len = grid[0].length;
        int[] result = new int[len];
        for (int i = 0; i < len; i++) {
            result[i] = i;
        }

        for (int[] row : grid) {
            // 遍历 result
            for (int i = 0; i < len; i++) {
                System.out.print(row[i] == 1 ? '╲' : '╱');
                int index = result[i];
                if (index == -1) {
                    continue;
                }
                int curDir = row[index];
                int nextIndex = index + curDir;
                int next;
                if (nextIndex == len || nextIndex == -1) {
                    next = -1 * curDir;
                } else {
                    next = row[nextIndex];
                }
                if (Math.abs(curDir + next) != 2) {
                    result[i] = -1;
                } else {
                    result[i] += curDir;
                }
            }
            System.out.print('\n');
        }

        return result;
    }

